package day14conditions;

public class Dowhileloop3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       /* local variable initialization */
		int n=1, times =0;
		/*do-while execution */
		do {
			System.out.println("java do while loops:"+ n);
			n++;
		}while(n<=times);
		
		}
	}


